icon_base64 = ""

stop_processing = False
queue = Queue()

def start_processing():
    global stop_processing
    stop_processing = False

    input_file = file_entry.get()
    input_folder = folder_entry.get()
    output_folder = output_entry.get()
    bg_color = color_entry.get()

    if not output_folder:
        messagebox.showwarning("Cảnh báo", "Vui lòng chọn thư mục lưu.")
        return

    if input_file:
        processing_thread = threading.Thread(target=lambda: process_file(input_file, output_folder, bg_color))
        processing_thread.start()
    elif input_folder:
        processing_thread = threading.Thread(target=lambda: process_folder(input_folder, output_folder, bg_color))
        processing_thread.start()
    else:
        messagebox.showwarning("Cảnh báo", "Vui lòng chọn file ảnh hoặc thư mục ảnh để xử lý.")
def process_file(input_file, output_folder, bg_color):
    try:
        file_name = os.path.basename(input_file)
        output_file = os.path.join(output_folder, os.path.splitext(file_name)[0] + ".png")
        bg_color_rgb = ImageColor.getrgb(bg_color) if bg_color else None
        processed_img = remove_background(input_file, bg_color_rgb)

        if not bg_color_rgb:
            processed_img.save(output_file)
        else:
            processed_img = processed_img.convert('RGB') if processed_img.mode == 'RGBA' else processed_img
            processed_img.save(output_file)
        
        queue.put((5, input_file, output_file))
    except Exception as e:
        queue.put((0, f"Không thể xử lý file {input_file}. Lỗi: {e}"))

def process_folder(input_folder, output_folder, bg_color):
    global stop_processing
    files = [f for f in os.listdir(input_folder) if f.lower().endswith(('.png', '.jpg', '.jpeg'))]
    total_files = len(files)
    
    if total_files == 0:
        messagebox.showwarning("Thông báo", "Không có tệp hình ảnh nào trong thư mục đã chọn.")
        return

    progress_bar['maximum'] = total_files
    processed_files = 0

    with ThreadPoolExecutor(max_workers=4) as executor:
        futures = [executor.submit(process_and_save_file, f, input_folder, output_folder, bg_color) for f in files]

        for i, future in enumerate(futures):
            if stop_processing:
                queue.put((2, "Quá trình đã dừng."))
                break
            
            future.result()
            processed_files += 1
            
            queue.put((3, processed_files, total_files))
    
    if not stop_processing:
        queue.put((4, "Tất cả các file trong thư mục đã được xử lý."))

def process_and_save_file(file_name, input_folder, output_folder, bg_color):
    input_file = os.path.join(input_folder, file_name)
    process_file(input_file, output_folder, bg_color)

def remove_background(input_path, bg_color=None):
    with open(input_path, "rb") as input_file:
        input_data = input_file.read()
        result = remove(input_data, alpha_matting=True, alpha_matting_foreground_threshold=240, alpha_matting_background_threshold=10)
        img = Image.open(io.BytesIO(result))

        if bg_color:
            background = Image.new('RGBA', img.size, bg_color)
            img = Image.alpha_composite(background, img)

        return img

def select_file():
    file_path = filedialog.askopenfilename(filetypes=[("Image Files", "*.png;*.jpg;*.jpeg")])
    if file_path:
        show_image(file_path, label_before)
        file_entry.delete(0, tk.END)
        file_entry.insert(0, file_path)

def select_folder(entry):
    folder_path = filedialog.askdirectory()
    if folder_path:
        entry.delete(0, tk.END)
        entry.insert(0, folder_path)

def select_color():
    color = colorchooser.askcolor()[1]
    if color:
        color_entry.delete(0, tk.END)
        color_entry.insert(0, color)

def show_image(image_path, label):
    img = Image.open(image_path)
    base_width = 200
    w_percent = (base_width / float(img.size[0]))
    h_size = int((float(img.size[1]) * float(w_percent)))
    img = img.resize((base_width, h_size), Image.Resampling.LANCZOS)
    
    img_tk = ImageTk.PhotoImage(img)
    label.config(image=img_tk, bg='white')
    label.image = img_tk

def stop_process():
    global stop_processing
    stop_processing = True
def update_gui():
    try:
        while True:
            message_type, *message = queue.get_nowait()

            if message_type == 1:  # Success message
                messagebox.showinfo("Thành công", message[0])
            elif message_type == 0:  # Error message
                messagebox.showerror("Lỗi", message[0])
            elif message_type == 2:  # Process stopped
                messagebox.showinfo("Dừng", message[0])
            elif message_type == 3:  # Progress update
                processed_files, total_files = message
                progress_bar['value'] = processed_files
                progress_label.config(text=f"Đã xử lý {processed_files}/{total_files} files")
            elif message_type == 4:  # Folder processing complete
                messagebox.showinfo("Thành công", message[0])
            elif message_type == 5:  # Cập nhật hình ảnh trước và sau khi xử lý
                input_file, output_file = message
                show_image(input_file, label_before)  # Hiển thị hình ảnh trước khi xử lý
                show_image(output_file, label_after)  # Hiển thị hình ảnh sau khi xử lý
            
            root.update_idletasks()

    except Empty:  # Sử dụng ngoại lệ Empty
        pass
    
    root.after(100, update_gui)
